class ShowDisplay extends React.Component {

    render() {

        var i,imgsrc,audiosrc;
        for(i=0;i<imagePath.length;i++)
        {
            if(imagePath[i].name === this.props.name)
            {
                imgsrc=imagePath[i].isrc;
                audiosrc=imagePath[i].asrc;
                break;
            }
        }
        let audio= new Audio(audiosrc)
        const start= () => {
            audio.play();
        }

        const stop= () => {
            audio.pause();
        }
        const headerStyle={
            color:'white',
            fontSize: 20,
            fontFamily: 'Ariel',
        }
        const bodyStyle={
            color:"white",
        }
        const cardBodyStyle={
            backgroundColor: "firebrick",
            width: "300px"
        }
        return (
            <Card style={{ width: '18rem', flex:1}}>
            <Card.Img variant="top" src={imgsrc} width="300" height="300" onMouseEnter={start} onMouseLeave={stop}/>
            <Card.Body style={cardBodyStyle}>
        <Card.Title style={headerStyle}>{this.props.name}</Card.Title>
            <Card.Text style={bodyStyle}>Some quick example text to build on the card title and make up the bulk ofthe card's content.
            </Card.Text>
            </Card.Body>
            </Card>
        )
    }
}

